function wpb_add_cookie_notice() {
    if (!isset($_COOKIE['cookie_notice_accepted']) || $_COOKIE['cookie_notice_accepted'] !== 'true') {
        get_template_part('/templates/cookies-banner');
        ?>
        <script type="text/javascript">
            function wpb_set_cookie() {
                document.cookie = "cookie_notice_accepted=true; expires=<?php echo time() + 86400 * 30; ?>; path=/";
                var cookieNotice = document.querySelector('.cookies-banner');
                if (cookieNotice) {
                    cookieNotice.remove();
                }
            }

            document.addEventListener('DOMContentLoaded', function() {
                var cookieNotice = document.querySelector('.cookies-banner');
                if (cookieNotice && document.cookie.indexOf('cookie_notice_accepted=true') > -1) {
                    cookieNotice.remove();
                }
            });
        </script>
        <?php
    }
}
add_action('wp_footer', 'wpb_add_cookie_notice');

function wpb_remove_cookie_notice() {
    if (isset($_COOKIE['cookie_notice_accepted']) && $_COOKIE['cookie_notice_accepted'] === 'true') {
        ?>
        <script type="text/javascript">
            document.addEventListener('DOMContentLoaded', function() {
                var cookieNotice = document.querySelector('.cookies-banner');
                if (cookieNotice) {
                    cookieNotice.remove();
                }
            });
        </script>
        <?php
    }
}
add_action('wp_enqueue_scripts', 'wpb_remove_cookie_notice');
